import json
from aiogram.types import InlineKeyboardButton, InlineKeyboardMarkup, WebAppInfo, ReplyKeyboardMarkup, KeyboardButton

keyboard_buttons = []
start = 1
end = 50

while start <= 583:
    label = f"Game {start} - {end}"
    keyboard_buttons.append(label)
    start = end + 1
    end = min(end + 50, 583)  # Limit the end value to 1167

# Create the main menu keyboard
main_menu = ReplyKeyboardMarkup(resize_keyboard=True, one_time_keyboard=True, row_width=2)

# Add the keyboard buttons to the main menu
main_menu.add(*[KeyboardButton(text=button_label) for button_label in keyboard_buttons])
main_menu.add(KeyboardButton(text="Pexels",web_app=WebAppInfo(url="https://www.pexels.com/")))

async def create_keyboard(start_number, end_number):
    # Load game data from JSON file
    with open('games_data/games_data.json', 'r', encoding='utf-8') as file:
        games_data = json.load(file)

    # Create a list of game names based on the user's specified range
    game_names = [games_data[str(i)]["Game Name"] for i in range(start_number, end_number + 1)]

    # Create KeyboardButton objects for the game names with 2 buttons per row
    keyboard_buttons = []
    row = []

    for i, game_name in enumerate(game_names, start=1):
        row.append(KeyboardButton(text=game_name))
        if i % 2 == 0 or i == len(game_names):
            keyboard_buttons.append(row)
            row = []

    # Add the "Ortga ðŸ”™" button as the last button if needed
    row.append(KeyboardButton(text="Ortga ðŸ”™"))
    keyboard_buttons.append(row)

    # Create a ReplyKeyboardMarkup with the generated buttons
    games = ReplyKeyboardMarkup(resize_keyboard=True, one_time_keyboard=True, row_width=2, keyboard=keyboard_buttons)

    return games

async def create_inline_web_button(game_name,game_link):

    inline_button = InlineKeyboardMarkup()
    inline_button.add(InlineKeyboardButton(text=f"{game_name}-ni o'ynash",web_app=WebAppInfo(url=game_link)))

    return inline_button


